import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux';
import {doSigninAsync,selectEmail,selectUserName,logout,selectToken} from './authenticationSlice'
// , authenticationSelector

const Authentication = () => {
    const dispatch = useDispatch();

    // const auth = useSelector(authenticationSelector);
    
    const email = useSelector(selectEmail);
    const userName = useSelector(selectUserName);
    const token = useSelector(selectToken);
    const [username, setusername] = useState('')
    const [password, setpassword] = useState('')

    return (
    <div className='myLogin'>
        {userName && <div>User name: {userName}</div>}
        {email && <div> Email: {email}</div>}
        {token && <div> token: {token}</div>}
        <hr/>
        Login
        
          <input type='text' value={username} placeholder='username' onChange={(e) => setusername(e.target.value)}></input>
          <input type='password' value={password} placeholder='password' onChange={(e) => setpassword(e.target.value)}></input>
          <button onClick={()=>dispatch(doSigninAsync({'u':username, 'p':password}))}>Login</button>
    
        {/* <button onClick={()=>dispatch(doSigninAsync())}>Login</button> */}
        <button onClick={()=>dispatch(logout())}>Logout</button>
    </div>
          //   <input
          // 
          //  
          //   value={incrementAmount}
          //   onChange={(e) => setIncrementAmount(e.target.value)}
          // />
          // <button
          //   className={styles.button}
          //   onClick={() => dispatch(incrementByAmount(incrementValue))}
          // >
  )
}

export default Authentication
