import React, { Fragment } from 'react'
import { useSelector, useDispatch } from 'react-redux';
import {doSigninAsync,selectEmail,selectUserName,logout,selectToken, authenticationSelector} from './authenticationSlice'

const Authentication = () => {
    const dispatch = useDispatch();

    // const auth = useSelector(authenticationSelector);
    
    const email = useSelector(selectEmail);
    const userName = useSelector(selectUserName);
    const token = useSelector(selectToken);

    const onSubmit = (data) => {
      dispatch(doSigninAsync(data));
    };
    
    return (
    <div className='myLogin'>
        {userName && <div>User name: {userName}</div>}
        {email && <div> Email: {email}</div>}
        {token && <div> token: {token}</div>}
        <hr/>
        Login
        <Fragment>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input type='text' name='username' placeholder='username'></input>
          <input type='password' name='password' placeholder='password'></input>
          <input type='submit'>sign in</input>
        </form>
        </Fragment>
        {/* <button onClick={()=>dispatch(doSigninAsync())}>Login</button> */}
        <button onClick={()=>dispatch(logout())}>Logout</button>
    </div>
          //   <input
