import { useDispatch, useSelector } from 'react-redux';
import { addToCart, booksSelector, dogetbooksAsync } from './main_shopSlice';



const MainShop = () => {
  const dispatch = useDispatch();
  const booksAr = useSelector(booksSelector);
  return (
    <div>
      <button onClick={() => dispatch(dogetbooksAsync())}>get Data</button>
      <hr />
      <div className="row row-cols-1 row-cols-md-2 g-5">
        {booksAr.map(item => (
          <div className="col" key={item.id}>
            <div className="card">
              <img src="./logo192.png" className="card-img-top" alt="..." />
              <div className="card-body">
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">book id: {item.id}<br />author: {item.author}<br />year_published: {item.year_published}</p>
                <button onClick={() => dispatch(addToCart(item))}>add to cart</button>
              </div>
            </div>
          </div>
        ))}

      </div>
    </div>
  )
}

export default MainShop
